const addscrollRestoration = (routes: RouteObject[]): RouteObject[] => {
	return routes.map((route) => {
		const wrappedElement = route.element ? (
			<React.Fragment>
				{route.element}
				<ScrollRestoration />
			</React.Fragment>
		) : (
			route.element
		);

		return {
			...route,
			element: wrappedElement,
		};
	});
};

